import React, { useState } from "react";
import SearchFilters from "../SearchFilters";
import Input from "../form/Input";
import Select from "../form/Select";

const Table = ({ tableMetaData, personData }) => {
  const [sortColumn, setSortColumn] = useState(null);
  const [sortDirection, setSortDirection] = useState("asc");
  const [filters, setFilters] = useState({
    name: "",
    age: "",
    occupation: "",
    gender: "",
    area: "",
  });

  const applyFilter = (data, filters) => {
    return data.filter((item) => {
      const nameMatch =
        !filters.name ||
        item.name.toLowerCase().includes(filters.name.toLowerCase());
      const ageMatch = !filters.age || item.age === parseInt(filters.age);
      const occupationMatch =
        !filters.occupation ||
        item.occupation
          .toLowerCase()
          .includes(filters.occupation.toLowerCase());
      const genderMatch =
        !filters.gender ||
        item.gender.toLowerCase() === filters.gender.toLowerCase();
      const areaMatch =
        !filters.area ||
        item.area.toLowerCase().includes(filters.area.toLowerCase());
      return (
        nameMatch && ageMatch && occupationMatch && genderMatch && areaMatch
      );
    });
  };

  const [sortedPersonData, setSortedPersonData] = useState(personData);
  const handleSort = (column) => {
    const filteredData = applyFilter(personData, filters);
    const sortedData = [...filteredData].sort((a, b) => {
      if (column === null) return 0; // No sorting
      const aValue = a[column];
      const bValue = b[column];
      if (aValue < bValue) return sortDirection === "asc" ? -1 : 1;
      if (aValue > bValue) return sortDirection === "asc" ? 1 : -1;
      return 0;
    });
    setSortedPersonData(sortedData);
  };

  const handleFilterChange = (event) => {
    const { name, value } = event.target;
    setFilters({ ...filters, [name]: value });
  };

  const renderSortingIcon = (fieldName) => {
    if (fieldName === sortColumn) {
      return sortDirection === "asc" ? <>&#8648;</> : <>&#8650;</>;
    }
    return <>&#8648;</>;
  };

  return (
    <div className="mt-2">
      {/* Filter input elements */}
      {/* <input
        type="text"
        name="name"
        value={filters.name}
        onChange={handleFilterChange}
        placeholder="Filter by name"
      />
      <input
        type="number"
        name="age"
        value={filters.age}
        onChange={handleFilterChange}
        placeholder="Filter by age"
      /> */}
      <div className="mt-5">
        <form className="bg-secondary p-3 text-white rounded">
          <div className="row">
            <div className="col-sm-3">
              <Input
                fieldName="name"
                placeholder="Search by Name"
                value={filters.name}
                onChange={handleFilterChange}
              />
            </div>
            <div className="col-sm-3">
              <Input
                fieldName="email"
                type="email"
                placeholder="Search by Email"
                value={filters.email}
                onChange={handleFilterChange}
              />
            </div>
            <div className="col-sm-3">
              <Input
                fieldName="phonenumber"
                type="tel"
                placeholder="Search by Phone Number"
                // value={filters.phoneNumber}
                onChange={handleFilterChange}
              />
            </div>
            {/* <div className="col-sm-3">
            <Input
              fieldName="age"
              label="Age"
              type="number"
              placeholder="Enter age"
              min="0"
              max="100"
            />
          </div> */}
            <div className="col-sm-3">
              <Select
                fieldName="gender"
                options={[
                  { label: "Male", value: "male" },
                  { label: "Female", value: "female" },
                ]}
              />
            </div>
            {/* <div className="col-sm-3">
            <Input
              fieldName="dateOfBirth"
              label="Date of Birth"
              type="date"
              placeholder="Enter date of birth"
            />
          </div> */}
          </div>
          <div className="row mt-3">
            <div className="col-sm-3">
              <Select
                fieldName="area"
                options={[
                  { value: "Mirpur", label: "Mirpur" },
                  { value: "Savar", label: "Savar" },
                  { value: "Banani", label: "Banani" },
                  { value: "Gulshan", label: "Gulshan" },
                  { value: "Dhanmondi", label: "Dhanmondi" },
                  { value: "MohakhaliDohs", label: "Mohakhali DOHS" },
                ]}
              />
            </div>
            <div className="col-sm-3">
              <Select
                fieldName="occupation"
                options={[
                  { value: "SoftwareEngineer", label: "Software Enginner" },
                  { value: "Banker", label: "Banker" },
                  { value: "Accountant", label: "Accountant" },
                  { value: "Architect", label: "Architect" },
                ]}
              />
            </div>
            <div className="col-sm-3 padding-left-12 mt-4">
              <button type="reset" className="btn btn-block btn-warning w-100">
                Cancel Filter
              </button>
            </div>
            <div className="col-sm-3 padding-left-12 mt-4">
              <button type="submit" className="btn btn-block btn-success w-100">
                <i className="bi bi-calender-2"></i>Apply Filter
              </button>
            </div>
          </div>
          {/* <div className="row ">
          <div className="d-flex justify-content-end">
            <div className="col-sm-3 padding-left-12">
              <button type="submit" className="btn btn-block btn-success w-100">
                Apply Filter
              </button>
            </div>
          </div>
        </div> */}
        </form>
      </div>
      {/* Add similar input elements for other optional fields */}

      <table className="table">
        <thead className="table-secondary">
          <tr>
            {tableMetaData.map((meta, i) => (
              <th
                className="cursor-pointer"
                key={i}
                onClick={() => handleSort(meta.fieldName)}
              >
                {meta.headerTitle}
                {meta.sortable && (
                  <span className="sorting-icon">
                    {renderSortingIcon(meta.fieldName)}
                  </span>
                )}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {applyFilter(personData, filters).map((rowData, index) => (
            <tr key={index}>
              {tableMetaData.map((meta, j) => (
                <td key={j}>{meta.render(rowData)}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Table;
