import React, { useState } from "react";

const Table = ({ tableMetaData, personData }) => {
  const [sortColumn, setSortColumn] = useState(null);
  const [sortDirection, setSortDirection] = useState("asc");

  const handleSort = (column) => {
    if (column === sortColumn) {
      setSortDirection(sortDirection === "asc" ? "desc" : "asc");
    } else {
      setSortColumn(column);
      setSortDirection("asc");
    }
  };

  const renderSortingIcon = (fieldName) => {
    if (fieldName === sortColumn) {
      return sortDirection === "asc" ? <>&uarr;</> : <>&darr;</>;
    }
    return null;
  };

  return (
    <div className="mt-2">
      <table className="table table-striped">
        <thead className="table-secondary">
          <tr>
            {tableMetaData.map((x, i) => (
              <th key={i} scope="col" onClick={handleSort(x.fieldName)}>
                {x.headerTitle} {renderSortingIcon(x.fieldName)}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {personData.map((rowData, index) => (
            <tr key={index}>
              {tableMetaData.map((meta, j) => (
                <td key={j}>{meta.render(rowData)}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Table;
