import React, { useState } from "react";
import CustomModal from "./CustomModal";
const TableContentHeader = ({ headingTitle, btnLabel }) => {
  const [showModal, setShowMoal] = useState(false);

  const handleOpenModal = () => {
    setShowMoal(true);
  };

  const handleCloseModal = () => {
    setShowMoal(false);
  };

  return (
    <>
      <div className="d-flex justify-content-between align-items-center mt-4">
        <h2>{headingTitle}</h2>

        {btnLabel && (
          <button
            type="button"
            onClick={handleOpenModal}
            className="btn btn-primary w-25 dtn-block"
          >
            {btnLabel}
          </button>
        )}
      </div>

      {showModal && <CustomModal show={showModal} onClose={handleCloseModal} />}
    </>
  );
};

export default TableContentHeader;
